// program for geting prime numbers using sieves #include <stdio.h> #include <stdlib.h> #include <math.h>  #define COLUMNS 6    int calc = 1; void print_number(int n) { 	int colu = COLUMNS; 	if (calc == colu)	// byter rad efter "COLUMNS" antal siffror 	{ 		printf("\n"); 		printf("%10d ", n); 		calc = 1; 	} 	else 	{ 		printf("%10d ", n); 		calc++; 	}  }  int print_sieves(int n) { 	print_number(5); }    // grund funktionen för att fråga till vilket tal som primtal ska listas int main(int argc, char *argv[]) { 	if (argc == 2) 		print_sieves(atoi(argv[1])); 	else 		printf("Please state an interger number.\n"); 	return 0; }